CREATE TABLE "migrations" ("id" integer primary key autoincrement not null, "migration" varchar not null, "batch" integer not null);

CREATE TABLE sqlite_sequence(name,seq);

CREATE TABLE "users" ("id" integer primary key autoincrement not null, "name" varchar not null, "email" varchar not null, "email_verified_at" datetime, "password" varchar not null, "role" varchar not null default 'user', "remember_token" varchar, "created_at" datetime, "updated_at" datetime);

CREATE TABLE "password_reset_tokens" ("email" varchar not null, "token" varchar not null, "created_at" datetime, primary key ("email"));

CREATE TABLE "sessions" ("id" varchar not null, "user_id" integer, "ip_address" varchar, "user_agent" text, "payload" text not null, "last_activity" integer not null, primary key ("id"));

CREATE TABLE "cache" ("key" varchar not null, "value" text not null, "expiration" integer not null, primary key ("key"));

CREATE TABLE "cache_locks" ("key" varchar not null, "owner" varchar not null, "expiration" integer not null, primary key ("key"));

CREATE TABLE "jobs" ("id" integer primary key autoincrement not null, "queue" varchar not null, "payload" text not null, "attempts" integer not null, "reserved_at" integer, "available_at" integer not null, "created_at" integer not null);

CREATE TABLE "job_batches" ("id" varchar not null, "name" varchar not null, "total_jobs" integer not null, "pending_jobs" integer not null, "failed_jobs" integer not null, "failed_job_ids" text not null, "options" text, "cancelled_at" integer, "created_at" integer not null, "finished_at" integer, primary key ("id"));

CREATE TABLE "failed_jobs" ("id" integer primary key autoincrement not null, "uuid" varchar not null, "connection" text not null, "queue" text not null, "payload" text not null, "exception" text not null, "failed_at" datetime not null default CURRENT_TIMESTAMP);

CREATE TABLE "product_extras" ("id" integer primary key autoincrement not null, "name" varchar not null, "description" text, "price" numeric not null, "category_id" integer, "image" varchar, "active" tinyint(1) not null default '1', "max_quantity" integer not null default '5', "is_default" tinyint(1) not null default '0', "created_at" datetime, "updated_at" datetime, "deleted_at" datetime, foreign key("category_id") references "categories"("id") on delete set null);

CREATE TABLE "product_product_extra" ("id" integer primary key autoincrement not null, "product_id" integer not null, "product_extra_id" integer not null, "created_at" datetime, "updated_at" datetime, foreign key("product_id") references "products"("id") on delete cascade, foreign key("product_extra_id") references "product_extras"("id") on delete cascade);

CREATE TABLE "categories" ("id" integer primary key autoincrement not null, "name" varchar not null, "slug" varchar not null, "description" text, "image" varchar, "sort_order" integer not null default '0', "active" tinyint(1) not null default '1', "is_daily_special" tinyint(1) not null default '0', "day_of_week" varchar, "created_at" datetime, "updated_at" datetime);

CREATE TABLE "products" ("id" integer primary key autoincrement not null, "category_id" integer not null, "name" varchar not null, "slug" varchar not null, "description" text, "image_path" varchar, "price" numeric not null default '0', "sizes" text, "max_toppings" integer, "free_toppings" integer default '0', "is_pizza" tinyint(1) not null default '0', "is_specialty" tinyint(1) not null default '0', "has_size_options" tinyint(1) not null default '0', "has_toppings" tinyint(1) not null default '0', "has_extras" tinyint(1) not null default '0', "active" tinyint(1) not null default '1', "is_featured" tinyint(1) not null default '0', "sort_order" integer not null default '0', "created_at" datetime, "updated_at" datetime, "add_ons" text, "is_combo" tinyint(1) not null default '0', "is_popular" tinyint(1) not null default '0', "display_day" varchar, "preselected_toppings" text, "is_customizable" tinyint(1) not null default '1', foreign key("category_id") references "categories"("id") on delete cascade);

CREATE TABLE "toppings" ("id" integer primary key autoincrement not null, "name" varchar not null, "category" varchar check ("category" in ('meat', 'veggie', 'cheese', 'sauce', 'spice')) not null default 'veggie', "counts_as" integer not null default '1', "price_factor" float not null default '1', "display_order" integer not null default '0', "is_active" tinyint(1) not null default '1', "created_at" datetime, "updated_at" datetime);

CREATE TABLE "combo_products" ("id" integer primary key autoincrement not null, "combo_id" integer not null, "product_id" integer not null, "quantity" integer not null default '1', "size" varchar, "max_toppings" integer not null default '0', "is_optional" tinyint(1) not null default '0', "additional_price" numeric not null default '0', "created_at" datetime, "updated_at" datetime, foreign key("combo_id") references "combos"("id") on delete cascade, foreign key("product_id") references "products"("id") on delete cascade);

CREATE TABLE "orders" ("id" integer primary key autoincrement not null, "order_number" varchar not null, "customer_name" varchar not null, "customer_phone" varchar not null, "customer_email" varchar, "pickup_time" datetime not null, "subtotal" numeric not null, "tax_amount" numeric not null default '0', "discount_amount" numeric not null default '0', "total" numeric not null, "payment_method" varchar not null default 'pickup', "payment_status" varchar not null default 'pending', "payment_id" varchar, "order_status" varchar not null default 'pending', "order_notes" text, "discount_id" integer, "created_at" datetime, "updated_at" datetime, foreign key("discount_id") references "discounts"("id") on delete set null);

CREATE TABLE "order_items" ("id" integer primary key autoincrement not null, "order_id" integer not null, "item_type" varchar not null, "item_id" integer not null, "name" varchar not null, "size" varchar, "quantity" integer not null, "unit_price" numeric not null, "subtotal" numeric not null, "options" text, "notes" text, "is_upsell" tinyint(1) not null default '0', "created_at" datetime, "updated_at" datetime, foreign key("order_id") references "orders"("id") on delete cascade);

CREATE TABLE "discounts" ("id" integer primary key autoincrement not null, "code" varchar not null, "description" varchar, "type" varchar check ("type" in ('fixed', 'percentage')) not null default 'fixed', "value" numeric not null, "usage_limit" integer, "usage_count" integer not null default '0', "expires_at" datetime, "active" tinyint(1) not null default '1', "created_at" datetime, "updated_at" datetime);

CREATE TABLE "product_toppings" ("id" integer primary key autoincrement not null, "product_id" integer not null, "topping_id" integer not null, "is_default" tinyint(1) not null default '0', "quantity" integer not null default '1', "size" varchar, "created_at" datetime, "updated_at" datetime, foreign key("product_id") references "products"("id") on delete cascade, foreign key("topping_id") references "toppings"("id") on delete cascade);

CREATE TABLE "settings" ("id" integer primary key autoincrement not null, "key" varchar not null, "value" text, "group" varchar not null default 'general', "type" varchar not null default 'text', "is_public" tinyint(1) not null default '1', "created_at" datetime, "updated_at" datetime);

CREATE TABLE "combo_product" ("id" integer primary key autoincrement not null, "combo_id" integer not null, "product_id" integer not null, "quantity" integer not null default '1', "size" varchar, "max_toppings" integer, "is_optional" tinyint(1) not null default '0', "additional_price" numeric not null default '0', "name" varchar, "is_pizza" tinyint(1) not null default '0', "created_at" datetime, "updated_at" datetime, foreign key("combo_id") references "combos"("id") on delete cascade, foreign key("product_id") references "products"("id") on delete cascade);

CREATE TABLE "combo_upsell_product" ("id" integer primary key autoincrement not null, "created_at" datetime, "updated_at" datetime);

CREATE TABLE "combos" ("id" integer primary key autoincrement not null, "name" varchar not null, "slug" varchar not null, "description" text, "image" varchar, "add_ons" text, "active" tinyint(1) not null default ('1'), "sort_order" integer not null default ('0'), "created_at" datetime, "updated_at" datetime, "product_id" integer not null, foreign key("product_id") references products("id") on delete cascade on update no action);

